settings.css:
.link-text {
    background-color: transparent !important;
    border: none;
}
.link-text:hover {
    background-color: transparent !important;
    text-decoration: underline;
}
.left-text-block {
    text-align:left !important
}
button.tile-button--side-icon div {
    display: inherit !important;
}
.text-left-to-icon {
    margin-left: 0.5em;
    vertical-align: super;
}
.field-message {
    font-size: 15px;
    min-width: auto;
}
/* only for default */
body, .page{
  background-color: #ededed !important;
}

.page{
  margin-top: 30px;
}

.field-message {
    font-size: 13px;
    min-width: auto;
}

sktemplate.inputSchema:
{
  "type": "array",
   "properties": {
        "devices": {
            "type": "object",
            "properties": {
                "id": {
                    "displayName": "id",
                        "preferedControlType": "textField",
                        "enableParameters": true
                },
                "type": {
                    "displayName": "type",
                        "preferedControlType": "textField",
                        "enableParameters": true
                },
                "status": {
                    "displayName": "status",
                        "preferedControlType": "textField",
                        "enableParameters": true
                }
            }
        },
        "pageTitle": {
            "type": "string",
            "displayName": "Page Title",
            "preferedControlType": "textField",
            "enableParameters": true
        },
        "canChangeAccount": {
            "type": "string",
            "displayName": "Show Switch Account",
            "preferedControlType": "toggleSwitch",
            "value": true
        }
    }
}

